# --- Fetch single geom type from DB using bounding box as clip patternfrom PostGISHelpers import *from WebOSM import *from region import *Wustermark = Region("Wustermark", boundary=(    12.871817013021891,    52.50091209200498,    13.018814242671711,    52.578008093778124))query_features = {'relation': 'brandenburg_polygon',                  'select_cols': ['osm_id', 'amenity'],                  'where_cond': "building is not NULL AND amenity is not NULL",                  'SRID': 4326}wustermark_buildings = OSMPolygons(name="Wustermark buildings",                                   region=Wustermark)wustermark_buildings.set_debug_level('warning')wustermark_buildings.create_where_query(**query_features)wustermark_buildings.fetch_geoms(source_db='postgres_andi@localhost:5432/osm')wustermark_buildings.plot_view()# wustermark_buildings.print_results()# wustermark_buildings.export2shp(#     '/Users/blubber/Documents/TEMP/wustermark_buildings.shp')# --- Fetch single geom type from DB using Shapefile as clip patternfrom PostGISHelpers import *from WebOSM import *from region import *Wustermark = Region("Wustermark",                    boundary='/Users/blubber/Documents/TEMP/wustermark_extend.shp')Wustermark_box = Region("Wustermark", boundary=(    12.871817013021891,    52.50091209200498,    13.018814242671711,    52.578008093778124))query_features = {'relation': 'brandenburg_polygon',                  'select_cols': ['osm_id', 'amenity'],                  'where_cond': "building is not NULL AND amenity is not NULL",                  'SRID': 4326}wustermark_buildings = OSMPolygons(name="Wustermark buildings",                                   region=Wustermark)wustermark_buildings.create_where_query(**query_features)wustermark_buildings.fetch_geoms(source_db='postgres_andi@localhost:5432/osm')wustermark_buildings.plot_view()wustermark_buildings.print_results()wustermark_buildings.export2shp(    '/Users/blubber/Documents/TEMP/wustermark_buildings.shp')# --- Fetch single geom type from full DB extentfrom PostGISHelpers import *from WebOSM import *from region import *Brandenburg = Region()query_features = {'relation': 'brandenburg_polygon',                  'select_cols': ['osm_id', 'amenity', 'name'],                  'where_cond': "building is not NULL AND amenity is not NULL",                  'SRID': 4326}brandenburg_buildings = OSMPolygons(name="Brandenburg buildings",                                    region=Brandenburg)brandenburg_buildings.create_where_query(**query_features)brandenburg_buildings.fetch_geoms(source_db='postgres_andi@localhost:5432/osm')brandenburg_buildings.plot_view()brandenburg_buildings.print_results()brandenburg_buildings.export2shp(    '/Users/blubber/Documents/TEMP/brandenburg_buildings.shp')# --- Fetch single geom type from DB using internal tablefrom PostGISHelpers import *from WebOSM import *from region import *Testregion = Region(name="Some Testregion", boundary='deutschland.testregion')query_features = {'relation': 'germany_polygon',                  'select_cols': ['osm_id', 'amenity', 'name'],                  'where_cond': "amenity is not NULL",                  'SRID': 4326}Testregion_shops = OSMPolygons(name="Testregion shops",                               region=Testregion)Testregion_shops.create_where_query(**query_features)Testregion_shops.fetch_geoms(source_db='Andi@192.168.10.25:5432/reiners_db')Testregion_shops.print_results()Testregion_shops.export2shp(    '/Users/blubber/Documents/TEMP/Testregion_amenities.shp')### Fetch all power data from complete OSM data set (Polygon, Line, Point)# at oncefrom PostGISHelpers import *from WebOSM import *from region import *Brandenburg = Region(name="Brandenburg")network = OSMCollection(name="Brandenburg Stromnetz", region=Brandenburg,                        points=False)network.create_collection_query(relation_prefix='brandenburg',                                select_cols=['osm_id', 'name', 'power'],                                where_cond="power is not NULL")network.fetch_OSM_collection(source_db='postgres_andi@localhost:5432/osm')network.plot_view(el_limit=5000)network.Lines.plot_view(el_limit=15000)#---import logging# create loggerlogger = logging.getLogger('simple_example')logger.setLevel(logging.WARNING)# create console handler and set level to debugch = logging.StreamHandler()# ch.setLevel(logging.WARNING)formatter = logging.Formatter('%(levelname)s: %(message)s')ch.setFormatter(formatter)logger.addHandler(ch)logger.debug('debug message')logger.info('info message')logger.warn('warn message')logger.error('error message')logger.critical('critical message')